function checkValid(field_id, field_range){
    var val = document.getElementById(field_id).value

    if (val < 0 || val > parseInt(field_range)){
        document.getElementById(field_id).value = ''
        alert('Please enter values <='+field_range)
    }
}

function select_radio_new(qid, element_id, ques_sec){
	var radio_element = document.getElementById('q'+qid+'_'+element_id)

	var element_count = json_data[ques_sec].data[parseInt(qid)-1]['ans'].length

	json_data[ques_sec].data[parseInt(qid)-1].selection_val = parseInt(element_id)

	if(ques_sec == 'covid_hospital' || ques_sec == 'dchc'){
		if(qid == '2' && element_id == '5'){
			document.getElementById('q_2_others_data').style.display = 'block'
		}else{
			document.getElementById('q_2_others_data').style.display = 'none'
		}
	}

	for(var i=0; i<element_count; i++){
		if((i+1) != parseInt(element_id)){
			//console.log('q'+qid+'_'+(i+1).toString())
			var ele = document.getElementById('q'+qid+'_'+(i+1).toString())
			ele.classList.remove("btn-info")
		}
	}

	radio_element.classList.add("btn-info")
}

